/*
 * Copyright (c) 2020.
 * Kamalita's coding
 */

import React, {Component} from 'react';

class HoverIncrement extends Component{
    constructor(props) {
        super(props);
        this.state={
            count:0
        }
    }

    increment=()=>{
            this.setState(
                prevState=>{
                    return prevState+1;
                }
            );

    }

    render() {
        return(

        <p onMouseMove={this.increment}>this text is hovered {this.state.count} times</p>

        );
    };


}
export default HoverIncrement;
